mapper.select:

SELECT seq, title, name, createdate, id, content, hit
FROM board
WHERE seq = #{seq}


SELECT *
FROM (SELECT seq, title, name, hit, createdate, ceil(rownum / 10) as page
		FROM board
		WHERE category=#{categorynum}
		ORDER BY seq DESC)
WHERE page = #{page}


SELECT password
FROM board
WHERE seq = #{seq}

mapper.update:
UPDATE board
SET title=#{title}, content=#{content}, ip=#{ip}, name=#{name}, password=#{password}, updatedate=SYSDATE
WHERE seq=#{seq}

mapper.select:

SELECT ceil(count(1) / 10)
FROM board
WHERE category = #{categorynum}


SELECT ceil(count(1) / 10)
FROM (SELECT ${searchArea}
		FROM board
		WHERE category=#{categorynum}
		 AND ${searchArea} LIKE '%'||#{search}||'%' )

mapper.update:

UPDATE board
SET hit = NVL(hit,0) + 1
WHERE seq = #{seq}
