mapper.select:

SELECT *
	FROM ( SELECT CEIL(ROWNUM/10) AS PAGE, Z.*
			FROM (SELECT seq, title, createdate, category 
					FROM notice_board 
					WHERE category = #{category} 
					ORDER BY seq DESC) Z
		)
	WHERE page = #{page}


SELECT *
	FROM ( SELECT CEIL(ROWNUM/10) AS PAGE, Z.*
			FROM (SELECT seq, title, createdate, category 
					FROM notice_board 
					WHERE category = #{category} 
					AND ${searchArea} LIKE '%'||#{search}||'%'
					ORDER BY seq DESC) Z
		)
	WHERE page = #{page}


SELECT ceil(count(1)/10)
FROM notice_board
WHERE category = #{category}
